import React, { Component } from 'react';
import "./mogura.css";
import mogura from './image/mogura.png';
import shibafu from './image/shibafu.png';

var mas = new Array(9);

export default class Todo extends Component  {

    constructor (props) {
        super(props);
        this.state = {
            location:1,
            result:0
        };
    }
    

    onClick = (id) => {
        const {location} = this.state;

        if(location )

        var a = Math.floor( Math.random() * 8)+1 ;
        console.log(a);

        var img = document.getElementById(id);
        img.src = shibafu;

        const IdNum = "Mas" + a;
        this.setState({
            location:a
        })

        var img = document.getElementById(IdNum);
        img.src = mogura;

    }
    
    
    render() {
        return (<div>
            <h1 className="title">&nbsp;
                <u>〇×ゲーム</u>
            </h1>
            <table>
                <tbody>
                    <tr>
                        <td><img id="Mas1" src={mogura}  onClick={() => this.onClick("Mas1" )} /></td>
                        <td><img id="Mas2" src={shibafu}  onClick={() => this.onClick("Mas2" )} /></td>
                        <td><img id="Mas3" src={shibafu}  onClick={() => this.onClick("Mas3" )} /></td>
                    </tr>
                    <tr>
                        <td><img id="Mas4" src={shibafu}  onClick={() => this.onClick("Mas4" )} /></td>
                        <td><img id="Mas5" src={shibafu}  onClick={() => this.onClick("Mas5" )} /></td>
                        <td><img id="Mas6" src={shibafu}  onClick={() => this.onClick("Mas6" )} /></td>
                    </tr>
                    <tr>
                        <td><img id="Mas7" src={shibafu}  onClick={() => this.onClick("Mas7" )} /></td>
                        <td><img id="Mas8" src={shibafu}  onClick={() => this.onClick("Mas8" )} /></td>
                        <td><img id="Mas9" src={shibafu}  onClick={() => this.onClick("Mas9" )} /></td>
                    </tr>
                </tbody>
            </table>
            <h1 id="TurnId" className="Message">プレイヤー1のターン</h1>
        </div>);
    }
  }
