import React, { Component } from 'react';
import "./mogura.css";
import mogura from './image/mogura2.png';
import shibafu from './image/shibafu.png';
import hit from './image/hit_mogura.png';

var mas = new Array(9);

export default class Todo extends Component  {

    constructor (props) {
        super(props);
        this.state = {
            location:"Mas1",
            result:0
        };
    }

    componentDidMount = () => {
        this.intervalId = setInterval(()=>{
            this.rand_MoguraUp();
        }, 1000);
      }

      componentWillUnmount(){
        clearInterval(this.intervalId);
      }
    

    onClick = (id) => {
        const {location} = this.state;

        if(location == id){
            var img = document.getElementById(id);
            img.src = hit;
        }
    }
    
    rand_MoguraUp = () =>{
        const {location} = this.state;

        var img = document.getElementById(location);
        img.src = shibafu;

        var a = Math.floor( Math.random() * 12)+1 ;
        console.log(a);

        const IdNum = "Mas" + a;
        this.setState({
            location:IdNum
        })

        var img = document.getElementById(IdNum);
        img.src = mogura;
    }

    
    render() {
        return (<div className="divCenter">
            <a className="title">
                <u>モグラたたきゲーム</u>
            </a>
            <button type="button" id="StButton"　className="StButton" onClick={this.onClick()}>スタート</button>
            <table>
                <tbody>
                    <tr>
                        <td><img id="Mas1" src={mogura}  onClick={() => this.onClick("Mas1" )} /></td>
                        <td><img id="Mas2" src={shibafu}  onClick={() => this.onClick("Mas2" )} /></td>
                        <td><img id="Mas3" src={shibafu}  onClick={() => this.onClick("Mas3" )} /></td>
                        <td><img id="Mas4" src={shibafu}  onClick={() => this.onClick("Mas4" )} /></td>
                    </tr>
                    <tr>
                        <td><img id="Mas5" src={shibafu}  onClick={() => this.onClick("Mas5" )} /></td>
                        <td><img id="Mas6" src={shibafu}  onClick={() => this.onClick("Mas6" )} /></td>
                        <td><img id="Mas7" src={shibafu}  onClick={() => this.onClick("Mas7" )} /></td>
                        <td><img id="Mas8" src={shibafu}  onClick={() => this.onClick("Mas8" )} /></td>
                    </tr>
                    <tr>
                        <td><img id="Mas9" src={shibafu}  onClick={() => this.onClick("Mas9" )} /></td>
                        <td><img id="Mas10" src={shibafu}  onClick={() => this.onClick("Mas10" )} /></td>
                        <td><img id="Mas11" src={shibafu}  onClick={() => this.onClick("Mas11" )} /></td>
                        <td><img id="Mas12" src={shibafu}  onClick={() => this.onClick("Mas12" )} /></td>
                    </tr>
                    <tr>
                        <td><img id="Mas13" src={shibafu}  onClick={() => this.onClick("Mas13" )} /></td>
                        <td><img id="Mas14" src={shibafu}  onClick={() => this.onClick("Mas14" )} /></td>
                        <td><img id="Mas15" src={shibafu}  onClick={() => this.onClick("Mas15" )} /></td>
                        <td><img id="Mas16" src={shibafu}  onClick={() => this.onClick("Mas16" )} /></td>
                    </tr>
                </tbody>
            </table>
                
            </div>
        );
    }
  }
